def check(bits):
    if len(bits)!=11:
        return False
    if bits[0]!='0' or bits[-1]!='1':
        return False
    data=bits[1:9]
    parity=bits[9]
    ones=data.count('1')+int(parity)
    return ones%2==0
print(check('00110111001'))

