import binascii, textwrap, sys, json, re, os

offset=9133337
nibble_len=8

def invduck(v):
    if 0<=v<=9:
        return str(v)
    elif 11<=v<=16:
        return 'abcdef'[v-11]
    else:
        raise ValueError('Invalid duck value')

def decode_enc(s):
    hexchars=[]
    for i in range(0,len(s),nibble_len):
        chunk=s[i:i+nibble_len]
        if len(chunk)<nibble_len:
            break
        num=int(chunk)-offset
        hexchars.append(invduck(num))
    hexstr=''.join(hexchars)
    return binascii.unhexlify(hexstr)

