import binascii, itertools, sys
from Crypto.Cipher import DES
IV=b'13371337'
weak_keys=[binascii.unhexlify(k) for k in ['0101010101010101','FEFEFEFEFEFEFEFE','E0E0E0E0F1F1F1F1','1F1F1F1F0E0E0E0E']]

offset=9133337; nlen=8

def invduck(v):
    if 0<=v<=9:
        return str(v)
    return 'abcdef'[v-11]

def decode(s):
    arr=[invduck(int(s[i:i+nlen])-offset) for i in range(0,len(s),nlen)]
    return binascii.unhexlify(''.join(arr))

cipher=decode(open('Challenge/DES2Bytes.enc').read().strip())
plain_target=open('Challenge/DES2Bytes.txt').read().strip().encode()
print(len(cipher), len(plain_target))

def apply(data, key):
    return DES.new(key, DES.MODE_OFB, IV).decrypt(data)

for k1,k2 in itertools.product(weak_keys, repeat=2):
    pt=apply(apply(cipher,k1),k2) # order variant
    if pt.rstrip(b'_')==plain_target:
        print('found order1', k1.hex(), k2.hex())
        sys.exit()
print('none order1')

